CREATE DATABASE learn_and_help_db;

USE learn_and_help_db;

CREATE TABLE classes (
     Class_Id int NOT NULL AUTO_INCREMENT,
     Class_Name varchar(30),
     Description TEXT,
     PRIMARY KEY (Class_Id));


CREATE TABLE registrations
	(Reg_Id int NOT NULL AUTO_INCREMENT,
	Sponsor_Name varchar(50),
	Sponsor_Email varchar(50),
	Sponsor_Phone_Number varchar(15),
	Spouse_Name varchar(50),
	Spouse_Email varchar(50),
	Spouse_Phone_Number varchar(15),
	Student_Name varchar(50),
	Student_Email varchar(50),
	Student_Phone_Number varchar(15),
	Class_Id int,
	Cause varchar(20),
	Modified_Time date,
	Created_Time date,
	PRIMARY KEY (Reg_Id),
	FOREIGN KEY (Class_Id) REFERENCES classes(Class_Id) ON DELETE CASCADE);



CREATE TABLE users (
	User_Id int NOT NULL AUTO_INCREMENT,
	First_Name varchar(30),
	Last_Name varchar(30),
	Email varchar(75),
	Phone varchar(15),
	Hash varchar(200),
	Active varchar(10),
	Role varchar(20),
	Modified_Time date,
    Created_Time date,
	PRIMARY KEY (User_Id));


CREATE TABLE blogs (
	Blog_Id int NOT NULL AUTO_INCREMENT,
	Title varchar(100),
	Author varchar(50),
	Description TEXT,
	Video_Link varchar(200),
	Modified_Time DATETIME,
	Created_Time DATETIME,
	PRIMARY KEY (Blog_Id));

CREATE TABLE blog_pictures (
	Picture_Id int NOT NULL AUTO_INCREMENT,
	Blog_Id int,
	Location varchar(100),
	PRIMARY KEY (Picture_Id),
	FOREIGN KEY (Blog_Id) REFERENCES blogs(Blog_Id));

CREATE TABLE causes (
	Cause_Id int NOT NULL AUTO_INCREMENT,
	Cause_name VARCHAR(100),
	description TEXT,
	URL VARCHAR(150),
	Contact_name VARCHAR(50),
	Contact_email VARCHAR(50),
	Contact_phone VARCHAR(15),
	PRIMARY KEY (Cause_Id)
);

