CREATE TABLE user_registrations (
	User_Id int,
	Reg_Id int,
	PRIMARY KEY (User_Id, Reg_Id),
	FOREIGN KEY (User_Id) REFERENCES users(User_Id) ON DELETE CASCADE,
	FOREIGN KEY (Reg_Id) REFERENCES registrations(Reg_Id) ON DELETE CASCADE
);

